import React, { useCallback, useEffect, useState, useRef } from "react";
import { Keyboard, StyleSheet, Switch, TextInput, Dimensions, ScrollView, Animated } from "react-native";
import Alert from "../components/Alert";
import { Text, View, TouchableOpacity } from "./Themed";
import { Ionicons } from "@expo/vector-icons";
import { Picker } from "@react-native-picker/picker";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Datetime from "react-datetime";
import { timedFrequencyOptions } from "../helpers/FrequencyOptions";
import { fetchAPI } from "../graphql/FetchAPI";
import {
    createCue,
    deleteCue,
    deleteForEveryone,
    getChannelCategories,
    getChannels,
    getQuiz,
    getSharedWith,
    markAsRead,
    shareCueWithMoreIds,
    start,
    submit
} from "../graphql/QueriesAndMutations";
import * as ImagePicker from "expo-image-picker";
import { actions, RichEditor, RichToolbar } from "react-native-pell-rich-editor";
import FileUpload from "./UploadFiles";
import Select from "react-select";
import { Collapse } from "react-collapse";
import Quiz from "./Quiz";
import { CountdownCircleTimer } from "react-countdown-circle-timer";
import TeXToSVG from "tex-to-svg";
import EquationEditor from "equation-editor-react";
// import WebView from "react-native-webview";
import { PreferredLanguageText } from "../helpers/LanguageContext";
import moment from "moment";
import ReactPlayer from "react-player";
import Webview from './Webview'

const UpdateControls: React.FunctionComponent<{ [label: string]: any }> = (props: any) => {
    const current = new Date();
    const [cue, setCue] = useState(props.cue.cue);
    const [shuffle, setShuffle] = useState(props.cue.shuffle);
    const [starred, setStarred] = useState(props.cue.starred);
    const [color, setColor] = useState(props.cue.color);
    const [notify, setNotify] = useState(props.cue.frequency !== "0" ? true : false);
    const [frequency, setFrequency] = useState(props.cue.frequency);
    const [customCategory, setCustomCategory] = useState(props.cue.customCategory);
    const [customCategories, setCustomCategories] = useState(props.customCategories);
    const [addCustomCategory, setAddCustomCategory] = useState(false);
    const [markedAsRead, setMarkedAsRead] = useState(false);
    const [reloadEditorKey, setReloadEditorKey] = useState(Math.random());
    const [isOwner, setIsOwner] = useState(false);
    const stopPlay =
        props.cue.endPlayAt && props.cue.endPlayAt !== ""
            ? props.cue.endPlayAt === "Invalid Date"
                ? new Date(current.getTime() + 1000 * 60 * 60)
                : new Date(props.cue.endPlayAt)
            : new Date(current.getTime() + 1000 * 60 * 60);
    const [endPlayAt, setEndPlayAt] = useState<Date>(stopPlay);
    const [playChannelCueIndef, setPlayChannelCueIndef] = useState(
        props.cue.endPlayAt && props.cue.endPlayAt !== "" ? false : true
    );
    const now = new Date(props.cue.date);
    const RichText: any = useRef();
    const [height, setHeight] = useState(100);
    const colorChoices: any[] = ["#d91d56", "#ED7D22", "#F8D41F", "#B8D41F", "#53BE6D"].reverse();
    const [submission, setSubmission] = useState(props.cue.submission ? props.cue.submission : false);
    const dead =
        props.cue.deadline && props.cue.deadline !== ""
            ? props.cue.deadline === "Invalid Date"
                ? new Date(current.getTime() + 1000 * 60 * 60 * 24)
                : new Date(props.cue.deadline)
            : new Date(current.getTime() + 1000 * 60 * 60 * 24);

    const initiate =
        props.cue.initiateAt && props.cue.initiateAt !== ""
            ? props.cue.initiateAt === "Invalid Date"
                ? new Date()
                : new Date(props.cue.initiateAt)
            : new Date();

    const [deadline, setDeadline] = useState<Date>(dead);
    const [initiateAt, setInitiateAt] = useState<Date>(initiate)
    const [gradeWeight, setGradeWeight] = useState<any>(props.cue.gradeWeight ? props.cue.gradeWeight : 0);
    const [score] = useState<any>(props.cue.score ? props.cue.score : 0);
    const [graded, setGraded] = useState(props.cue.gradeWeight && props.cue.gradeWeight !== 0 ? true : false);
    const currentDate = new Date();
    const [submitted, setSubmitted] = useState(false);
    const [userSetupComplete, setUserSetupComplete] = useState(false);
    const [imported, setImported] = useState(false);
    const [url, setUrl] = useState("");
    const [type, setType] = useState("");
    const [title, setTitle] = useState("");
    const [submissionImported, setSubmissionImported] = useState(false);
    const [submissionUrl, setSubmissionUrl] = useState("");
    const [showOptions, setShowOptions] = useState(false);
    const [submissionType, setSubmissionType] = useState("");
    const [submissionTitle, setSubmissionTitle] = useState("");
    const [key, setKey] = useState(Math.random());
    const [showImportOptions, setShowImportOptions] = useState(false);
    const [channels, setChannels] = useState<any[]>([]);
    const [shareWithChannelId, setShareWithChannelId] = useState("");
    const [selected, setSelected] = useState<any[]>([]);
    const [subscribers, setSubscribers] = useState<any[]>([]);
    const [expandMenu, setExpandMenu] = useState(false);
    const [original, setOriginal] = useState(props.cue.original)

    // QUIZ OPTIONS
    const [isQuiz, setIsQuiz] = useState(false);
    const [problems, setProblems] = useState<any[]>([]);
    const [solutions, setSolutions] = useState<any[]>([]);
    const [quizId, setQuizId] = useState("");
    const [loading, setLoading] = useState(true);
    const [initiatedAt, setInitiatedAt] = useState<any>(null);
    const [isQuizTimed, setIsQuizTimed] = useState(false);
    const [duration, setDuration] = useState(0);
    const [initDuration, setInitDuration] = useState(0);
    const [equation, setEquation] = useState("y = x + 1");
    const [showEquationEditor, setShowEquationEditor] = useState(false);
    const [shuffleQuiz, setShuffleQuiz] = useState(false);

    const insertEquation = useCallback(() => {
        const SVGEquation = TeXToSVG(equation, { width: 100 }); // returns svg in html format
        RichText.current.insertHTML("<div><br/>" + SVGEquation + "<br/></div>");
        setShowEquationEditor(false);
        setEquation("");
        // setReloadEditorKey(Math.random())
    }, [equation, RichText, RichText.current, cue]);

    const diff_seconds = (dt2: any, dt1: any) => {
        var diff = (dt2.getTime() - dt1.getTime()) / 1000;
        return Math.abs(Math.round(diff));
    };
    // ALERTS
    const unableToStartQuizAlert = PreferredLanguageText("unableToStartQuiz");
    const deadlineHasPassedAlert = PreferredLanguageText("deadlineHasPassed");
    const enterTitleAlert = PreferredLanguageText("enterTitle");
    const cueDeletedAlert = PreferredLanguageText("cueDeleted");
    const submissionFailedAlert = PreferredLanguageText("submissionFailed");
    const ifYouStartTimedQuizAlert = PreferredLanguageText("ifYouStartTimedQuiz");
    const submissionCompleteAlert = PreferredLanguageText("submissionComplete");
    const tryAgainLaterAlert = PreferredLanguageText("tryAgainLater");
    const somethingWentWrongAlert = PreferredLanguageText("somethingWentWrong");
    const clearQuestionAlert = PreferredLanguageText("clearQuestion");
    const cannotUndoAlert = PreferredLanguageText("cannotUndo");
    const sharedAlert = PreferredLanguageText("sharedAlert");
    const checkConnectionAlert = PreferredLanguageText("checkConnection");

    // ON INIT = LOAD CHANNEL RELATED CONTENT
    useEffect(() => {
        loadChannelsAndSharedWith();
    }, []);

    // Used to detect ongoing quiz and
    useEffect(() => {
        if (!isQuizTimed || initiatedAt === null || initiatedAt === "" || isOwner) {
            // not a timed quiz or its not been initiated
            return;
        }
        let now = new Date();
        now.setMinutes(now.getMinutes() - 1);
        let current = new Date();
        if (now >= deadline) {
            // deadline crossed
            return;
        }
        if (duration === 0) {
            return;
        }
        const remainingTime = duration - diff_seconds(initiatedAt, current);
        if (remainingTime <= 0) {
            // duration has been set correctly yet no time remaining
            if (!props.cue.submittedAt || props.cue.submittedAt === "") {
                handleSubmit();
            }
        } else {
            setInitDuration(remainingTime); // set remaining duration in seconds
        }
    }, [initiatedAt, duration, deadline, isQuizTimed, props.cue.submittedAt, isOwner]);

    // Loads all the channel categories and list of people cue has been shared with
    const loadChannelsAndSharedWith = useCallback(async () => {
        const uString: any = await AsyncStorage.getItem("user");
        if (uString) {
            const user = JSON.parse(uString);
            const server = fetchAPI("");

            if (props.channelId) {
                server
                    .query({
                        query: getChannelCategories,
                        variables: {
                            channelId: props.channelId
                        }
                    })
                    .then(res => {
                        if (res.data.channel && res.data.channel.getChannelCategories) {
                            setCustomCategories(res.data.channel.getChannelCategories);
                        }
                    })
                    .catch(err => { });
            }

            server
                .query({
                    query: getChannels,
                    variables: {
                        userId: user._id
                    }
                })
                .then(res => {
                    if (res.data.channel.findByUserId) {
                        setChannels(res.data.channel.findByUserId);
                    }
                })
                .catch(err => { });
            if (user._id.toString().trim() === props.cue.createdBy && props.cue.channelId && props.cue.channelId !== "") {
                // owner
                server
                    .query({
                        query: getSharedWith,
                        variables: {
                            channelId: props.cue.channelId,
                            cueId: props.cue._id
                        }
                    })
                    .then((res: any) => {
                        if (res.data && res.data.cue.getSharedWith) {
                            setSubscribers(res.data.cue.getSharedWith);
                            // clear selected
                            const sel = res.data.cue.getSharedWith.filter((item: any) => {
                                return item.isFixed;
                            });
                            setSelected(sel);
                        }
                    })
                    .catch((err: any) => console.log(err));
            }
        }
    }, [props.cue, props.channelId]);

    // If cue contains a Quiz, then need to fetch the quiz and set State
    useEffect(() => {
        if (props.cue.channelId && props.cue.channelId !== "") {
            const data1 = original;
            const data2 = cue;
            if (data2 && data2[0] && data2[0] === "{" && data2[data2.length - 1] === "}") {
                const obj = JSON.parse(data2);
                setSubmissionImported(true);
                setSubmissionUrl(obj.url);
                setSubmissionType(obj.type);
                if (loading) {
                    setSubmissionTitle(obj.title);
                }
            } else {
                setSubmissionImported(false);
                setSubmissionUrl("");
                setSubmissionType("");
                setSubmissionTitle("");
            }
            if (data1 && data1[0] && data1[0] === "{" && data1[data1.length - 1] === "}") {
                const obj = JSON.parse(data1);
                if (obj.quizId) {
                    if (!loading) {
                        return;
                    }
                    // if (isQuiz) {
                    //     return;
                    // }
                    // setShowOptions(true);
                    // load quiz here and set problems
                    const server = fetchAPI("");
                    server
                        .query({
                            query: getQuiz,
                            variables: {
                                quizId: obj.quizId
                            }
                        })
                        .then(res => {
                            if (res.data && res.data.quiz.getQuiz) {
                                setQuizId(obj.quizId);
                                const solutionsObject = cue ? JSON.parse(cue) : {};
                                if (solutionsObject.solutions) {
                                    setSolutions(solutionsObject.solutions);
                                }
                                setProblems(res.data.quiz.getQuiz.problems);
                                if (res.data.quiz.getQuiz.duration && res.data.quiz.getQuiz.duration !== 0) {
                                    setDuration(res.data.quiz.getQuiz.duration * 60);
                                    setIsQuizTimed(true);
                                }
                                if (solutionsObject.initiatedAt && solutionsObject.initiatedAt !== "") {
                                    const init = new Date(solutionsObject.initiatedAt);
                                    setInitiatedAt(init);
                                }
                                setShuffleQuiz(res.data.quiz.getQuiz.shuffleQuiz ? true : false)
                                setTitle(obj.title);
                                setIsQuiz(true);
                                setLoading(false);
                            }
                        });
                } else {
                    setImported(true);
                    setType(obj.type);
                    if (loading) {
                        setTitle(obj.title);
                    }
                    setUrl(obj.url);
                    setKey(Math.random());
                }
            } else {
                setImported(false);
                setUrl("");
                setType("");
                setTitle("");
            }
        } else {
            const data = cue;
            if (data && data[0] && data[0] === "{" && data[data.length - 1] === "}") {
                const obj = JSON.parse(data);
                setSubmissionImported(true);
                setSubmissionUrl(obj.url);
                setSubmissionType(obj.type);
                setSubmissionTitle(obj.title);
            } else {
                setSubmissionImported(false);
                setSubmissionUrl("");
                setSubmissionType("");
                setSubmissionTitle("");
            }
        }
        setLoading(false);
    }, [props.cue, cue, loading, original]);

    useEffect(() => {
        console.log("Handle update called", new Date().toDateString());
        handleUpdate();
    }, [
        cue,
        shuffle,
        frequency,
        starred,
        color,
        props.cueIndex,
        submitted,
        markedAsRead,
        submission,
        deadline,
        initiateAt,
        submissionTitle,
        submissionImported,
        submissionType,
        submissionUrl,
        isQuiz,
        solutions,
        initiatedAt,
        customCategory,
        props.cueKey,
        endPlayAt,
        playChannelCueIndef,
        notify,
        url, original, type, title, imported
    ]);

    const handleHeightChange = useCallback((h: any) => {
        setHeight(h);
    }, []);

    // Camera for Toolbar
    const cameraCallback = useCallback(async () => {
        const cameraSettings = await ImagePicker.getCameraPermissionsAsync();
        if (!cameraSettings.granted) {
            await ImagePicker.requestCameraPermissionsAsync();
            const updatedCameraSettings = await ImagePicker.getCameraPermissionsAsync();
            if (!updatedCameraSettings.granted) {
                return;
            }
        }

        let result = await ImagePicker.launchCameraAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            quality: 1,
            base64: true
        });
        if (!result.cancelled) {
            RichText.current.insertImage(result.uri, "border-radius: 8px");
        }
    }, [RichText, RichText.current]);

    // Gallery for Toolbar
    const galleryCallback = useCallback(async () => {
        const gallerySettings = await ImagePicker.getMediaLibraryPermissionsAsync();
        if (!gallerySettings.granted) {
            await ImagePicker.requestMediaLibraryPermissionsAsync();
            const updatedGallerySettings = await ImagePicker.getMediaLibraryPermissionsAsync();
            if (!updatedGallerySettings.granted) {
                return;
            }
        }
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            quality: 1,
            base64: true
        });
        if (!result.cancelled) {
            RichText.current.insertImage(result.uri, "border-radius: 8px");
        }
    }, [RichText, RichText.current]);

    const initQuiz = useCallback(async () => {
        let now = new Date();
        if (now >= deadline) {
            Alert(unableToStartQuizAlert, deadlineHasPassedAlert);
            return;
        }

        if (now < initiateAt) {
            Alert("Quiz not available")
            return;
        }

        const u = await AsyncStorage.getItem("user");
        if (u) {
            const user = JSON.parse(u);
            const now = new Date();
            const server = fetchAPI("");
            const saveCue = JSON.stringify({
                solutions,
                initiatedAt: now
            });
            server
                .mutate({
                    mutation: start,
                    variables: {
                        cueId: props.cue._id,
                        userId: user._id,
                        cue: saveCue
                    }
                })
                .then(res => {
                    if (res.data.quiz.start) {
                        setInitiatedAt(now);
                    }
                })
                .catch(err => console.log(err));
            // save time to cloud first
            // after saving time in cloud, save it locally, set initiatedAt
            // quiz gets triggered
        }
    }, [props.cue._id, solutions, deadline, initiateAt]);

    // Handle Update for CUES. Called everytime there is a cue modification
    // Overrides local cues in AsyncStorage and then calls reloadCuesAfterList to sync with the cloud
    const handleUpdate = useCallback(async () => {

        // If available From set after Deadline or Dealine set before Available then 
        // we need to throw an Alert and return without updating

        let subCues: any = {};
        try {
            const value = await AsyncStorage.getItem("cues");
            if (value) {
                subCues = JSON.parse(value);
            }
        } catch (e) { }
        if (subCues[props.cueKey].length === 0) {
            return;
        }
        let saveCue = "";
        if (isQuiz) {
            saveCue = JSON.stringify({
                solutions,
                initiatedAt
            });
        } else if (submissionImported) {
            const obj = {
                type: submissionType,
                url: submissionUrl,
                title: submissionTitle
            };
            saveCue = JSON.stringify(obj);
        } else {
            saveCue = cue;
        }
        const submittedNow = new Date();

        let tempOriginal = ''
        if (imported) {
            const obj = {
                type,
                url,
                title
            }
            tempOriginal = JSON.stringify(obj)
        } else {
            tempOriginal = original
        }

        subCues[props.cueKey][props.cueIndex] = {
            _id: props.cue._id,
            cue: props.cue.submittedAt ? props.cue.cue : saveCue,
            date: props.cue.date,
            color,
            shuffle,
            frequency,
            starred,
            customCategory,
            // Channel controls
            channelId: props.cue.channelId,
            createdBy: props.cue.createdBy,
            endPlayAt: notify && (shuffle || !playChannelCueIndef) ? endPlayAt.toISOString() : "",
            channelName: props.cue.channelName,
            original: tempOriginal,
            status: "read",
            graded: props.cue.graded,
            gradeWeight,
            submission,
            unreadThreads: props.cue.unreadThreads,
            score,
            comment: props.cue.comment,
            submittedAt: submitted ? submittedNow.toISOString() : props.cue.submittedAt,
            deadline: submission ? deadline.toISOString() : "",
            initiateAt: submission ? initiateAt.toISOString() : "",
        };
        const stringifiedCues = JSON.stringify(subCues);
        await AsyncStorage.setItem("cues", stringifiedCues);
        props.reloadCueListAfterUpdate();
    }, [
        cue,
        customCategory,
        shuffle,
        frequency,
        starred,
        color,
        playChannelCueIndef,
        notify,
        submissionImported,
        submission,
        deadline,
        gradeWeight,
        submitted,
        submissionTitle,
        submissionType,
        submissionUrl,
        isQuiz,
        props.closeModal,
        props.cueIndex,
        props.cueKey,
        props.cue,
        endPlayAt,
        props,
        solutions,
        initiatedAt,
        // submission,
        // deadline,
        initiateAt,
        title, original, imported, type, url
    ]);

    // Handle Delete Cue
    const handleDelete = useCallback(async () => {
        Alert("Delete cue?", "", [
            {
                text: "Cancel",
                style: "cancel",
                onPress: () => {
                    return;
                }
            },
            {
                text: "Okay",
                onPress: async () => {
                    const server = fetchAPI("");
                    if (props.cue.channelId && isOwner) {
                        server
                            .mutate({
                                mutation: deleteForEveryone,
                                variables: {
                                    cueId: props.cue._id
                                }
                            })
                            .then(res => {
                                if (res.data.cue.deleteForEveryone) {
                                    Alert(cueDeletedAlert);
                                }
                            });
                    }

                    if (!props.cue.channelId) {
                        server.mutate({
                            mutation: deleteCue,
                            variables: {
                                cueId: props.cue._id
                            }
                        });
                    }

                    let subCues: any = {};
                    try {
                        const value = await AsyncStorage.getItem("cues");
                        if (value) {
                            subCues = JSON.parse(value);
                        }
                    } catch (e) { }
                    if (subCues[props.cueKey].length === 0) {
                        return;
                    }
                    const updatedCues: any[] = [];
                    subCues[props.cueKey].map((i: any, j: any) => {
                        if (j !== props.cueIndex) {
                            updatedCues.push({ ...i });
                        }
                    });
                    subCues[props.cueKey] = updatedCues;
                    const stringifiedCues = JSON.stringify(subCues);
                    await AsyncStorage.setItem("cues", stringifiedCues);
                    props.closeModal();
                }
            }
        ]);
    }, [props.cueIndex, props.closeModal, props.cueKey, props.cue, isOwner]);

    // Handle Submit for Submissions and Quizzes
    const handleSubmit = useCallback(async () => {
        if (!isQuiz && submissionImported && submissionTitle === "") {
            Alert("Your submission has no title");
            return;
        }

        Alert("Submit?", "", [
            {
                text: "Cancel",
                style: "cancel",
                onPress: () => {
                    return;
                }
            },
            {
                text: "Okay",
                onPress: async () => {
                    const u: any = await AsyncStorage.getItem("user");
                    let now = new Date();
                    // one minute of extra time to submit
                    now.setMinutes(now.getMinutes() - 1);
                    if (isQuiz) {
                        if (now >= deadline) {
                            Alert(submissionFailedAlert, ifYouStartTimedQuizAlert);
                            return;
                        }
                        // over here check that all options have been selected
                        // TO DO
                    } else {
                        if (now >= deadline) {
                            Alert(submissionFailedAlert, deadlineHasPassedAlert);
                            return;
                        }
                    }
                    if (u) {
                        const parsedUser = JSON.parse(u);
                        if (!parsedUser.email || parsedUser.email === "") {
                            // cannot submit
                            return;
                        }
                        let saveCue = "";
                        if (isQuiz) {
                            saveCue = JSON.stringify({
                                solutions,
                                initiatedAt
                            });
                            console.log("Save Cue", saveCue)
                        } else if (submissionImported) {
                            const obj = {
                                type: submissionType,
                                url: submissionUrl,
                                title: submissionTitle
                            };
                            saveCue = JSON.stringify(obj);
                        } else {
                            if (cue === "") {
                                // submission cannot be empty
                                return;
                            }
                            saveCue = cue;
                        }

                        const server = fetchAPI("");
                        server
                            .mutate({
                                mutation: submit,
                                variables: {
                                    cue: saveCue,
                                    cueId: props.cue._id,
                                    userId: parsedUser._id,
                                    quizId: isQuiz ? quizId : null
                                }
                            })
                            .then(res => {
                                if (res.data.cue.submitModification) {
                                    Alert(submissionCompleteAlert, new Date().toString(), [
                                        {
                                            text: "Cancel",
                                            style: "cancel",
                                            onPress: () => window.location.reload()
                                        },
                                        {
                                            text: "Okay",
                                            onPress: () => window.location.reload()
                                        }
                                    ]);
                                }
                            })
                            .catch(err => {
                                Alert(somethingWentWrongAlert, tryAgainLaterAlert);
                            });
                    }
                }
            }
        ]);
    }, [
        props.cue,
        cue,
        submissionTitle,
        submissionType,
        submissionUrl,
        submissionImported,
        isQuiz,
        quizId,
        initiatedAt,
        solutions,
        deadline
    ]);

    // SET IS OWNER AND SETUP COMPLETE
    useEffect(() => {
        (async () => {
            const u = await AsyncStorage.getItem("user");
            if (u && props.cue.createdBy) {
                const parsedUser = JSON.parse(u);
                if (parsedUser._id.toString().trim() === props.cue.createdBy.toString().trim()) {
                    setIsOwner(true);
                }
                if (parsedUser.email && parsedUser.email !== "") {
                    setUserSetupComplete(true);
                }
            }
        })();
    }, [props.cue]);

    const updateStatusAsRead = useCallback(async () => {
        if (props.cue.status && props.cue.status !== "read" && !markedAsRead) {
            const u = await AsyncStorage.getItem("user");
            if (u) {
                const user = JSON.parse(u);
                const server = fetchAPI("");
                server
                    .mutate({
                        mutation: markAsRead,
                        variables: {
                            cueId: props.cue._id,
                            userId: user._id
                        }
                    })
                    .then(res => {
                        if (res.data.status.markAsRead) {
                            setMarkedAsRead(true);
                        }
                    })
                    .catch(err => { });
            }
        }
    }, [props.cue, markedAsRead]);

    const clearAll = useCallback(() => {
        Alert(clearQuestionAlert, cannotUndoAlert, [
            {
                text: "Cancel",
                style: "cancel"
            },
            {
                text: "Clear",
                onPress: () => {
                    if (props.showOriginal) {
                        setImported(false)
                        setOriginal('')
                        setUrl('')
                        setType('')
                        setTitle('')
                    } else {
                        setSubmissionImported(false)
                        setCue('')
                        setSubmissionUrl('')
                        setSubmissionType('')
                        setSubmissionTitle('')
                    }
                    setReloadEditorKey(Math.random());
                }
            }
        ]);
    }, [props.showOriginal]);

    const shareCue = useCallback(async () => {
        let saveCue = "";
        if (submissionImported) {
            const obj = {
                type: submissionType,
                url: submissionUrl,
                title: submissionTitle
            };
            saveCue = JSON.stringify(obj);
        } else {
            saveCue = cue;
        }

        let tempOriginal = ''
        if (imported) {
            const obj = {
                type,
                url,
                title
            }
            tempOriginal = JSON.stringify(obj)
        } else {
            tempOriginal = original
        }

        const server = fetchAPI("");
        server
            .mutate({
                mutation: createCue,
                variables: {
                    cue: props.cue.channelId ? tempOriginal : saveCue,
                    starred,
                    color: color.toString(),
                    channelId: shareWithChannelId,
                    frequency,
                    customCategory,
                    shuffle,
                    createdBy: props.cue.createdBy,
                    gradeWeight: gradeWeight.toString(),
                    submission,
                    deadline: submission ? deadline.toISOString() : "",
                    endPlayAt: notify && (shuffle || !playChannelCueIndef) ? endPlayAt.toISOString() : ""
                }
            })
            .then(res => {
                if (res.data.cue.create) {
                    Alert(sharedAlert, "Cue has been successfully shared.");
                }
            })
            .catch(err => {
                Alert(somethingWentWrongAlert, checkConnectionAlert);
            });
    }, [
        submissionImported,
        submissionTitle,
        submissionType,
        submissionUrl,
        url, type, imported, title, original,
        cue,
        starred,
        color,
        frequency,
        customCategory,
        shuffle,
        gradeWeight,
        submission,
        deadline,
        notify,
        playChannelCueIndef,
        endPlayAt,
        shareWithChannelId,
        props.cue
    ]);

    const onChange = useCallback(
        (value, { action, option, removedValue }) => {
            switch (action) {
                case "remove-value":
                case "select-option":
                    const server = fetchAPI("");
                    server
                        .mutate({
                            mutation: shareCueWithMoreIds,
                            variables: {
                                cueId: props.cue._id,
                                userId: option.value
                            }
                        })
                        .then(res => {
                            if (res.data && res.data.cue.shareCueWithMoreIds) {
                                loadChannelsAndSharedWith();
                            }
                        })
                        .catch(err => console.log(err));
                    return;
                case "pop-value":
                    if (removedValue.isFixed) {
                        return;
                    }
                    break;
                case "clear":
                    value = subscribers.filter(v => v.isFixed);
                    break;
            }
            setSelected(value);
        },
        [subscribers, props.cue]
    );

    useEffect(() => {
        updateStatusAsRead();
    }, [props.cue.status]);

    const yesterday = moment().subtract(1, "day");
    const disablePastDt = (current: any) => {
        return current.isAfter(yesterday);
    };

    const width = Dimensions.get("window").width;

    if (loading) {
        return null;
    }

    // RENDER METHODS

    const renderRichToolbar = () => {
        return (props.cue.channelId && props.cue.channelId !== '' && !isOwner && props.showOriginal) || (props.showOriginal && showImportOptions) || isQuiz ? (
            <View style={{ height: 0, backgroundColor: "#fff" }} />
        ) : (((props.cue.graded && submission && !isOwner) || (currentDate > deadline && submission)) && !props.showOriginal) || (!props.showOriginal && showImportOptions) ? (
            <View style={{ height: 0, backgroundColor: "#fff" }} />
        ) : (
            <RichToolbar
                key={reloadEditorKey.toString() + props.showOriginal.toString()}
                style={{
                    flexWrap: "wrap",
                    backgroundColor: "white",
                    height: 28,
                    overflow: "visible"
                }}
                iconSize={12}
                editor={RichText}
                disabled={false}
                iconTint={"#a2a2aa"}
                selectedIconTint={"#a2a2aa"}
                disabledIconTint={"#a2a2aa"}
                actions={
                    (!props.showOriginal && submissionImported) || (imported && props.showOriginal)
                        ? ['']
                        : [
                            actions.setBold,
                            actions.setItalic,
                            actions.setUnderline,
                            actions.insertBulletsList,
                            actions.insertOrderedList,
                            actions.checkboxList,
                            actions.insertLink,
                            actions.insertImage,
                            "insertCamera",
                            actions.undo,
                            actions.redo,
                            "clear"
                        ]
                }
                iconMap={{
                    ["insertCamera"]: ({ tintColor }) => <Ionicons name="camera-outline" size={15} color={tintColor} />,
                    ["clear"]: ({ tintColor }) => (
                        <Ionicons name="trash-outline" size={13} color={tintColor} onPress={() => clearAll()} />
                    )
                }}
                onPressAddImage={galleryCallback}
                insertCamera={cameraCallback}
            />
        )
    };

    const renderEquationEditor = () => {
        return showEquationEditor ? (
            <View
                style={{
                    width: "100%",
                    flexDirection: width < 768 ? "column" : "row",
                    paddingBottom: 20
                }}>
                <View
                    style={{
                        borderColor: "#f4f4f6",
                        borderWidth: 1,
                        borderRadius: 15,
                        padding: 10,
                        minWidth: 200,
                        maxWidth: "50%"
                    }}>
                    <EquationEditor
                        value={equation}
                        onChange={setEquation}
                        autoCommands="pi theta sqrt sum prod alpha beta gamma rho int"
                        autoOperatorNames="sin cos tan arccos arcsin arctan"
                    />
                </View>
                <TouchableOpacity
                    style={{
                        justifyContent: "center",
                        paddingHorizontal: 20,
                        maxWidth: "10%"
                    }}
                    onPress={() => insertEquation()}>
                    <Ionicons name="add-circle-outline" color="#a2a2aa" size={20} />
                </TouchableOpacity>
                <View
                    style={{
                        minWidth: "40%",
                        flex: 1,
                        paddingVertical: 5,
                        justifyContent: "center"
                    }}>
                    <Text style={{ flex: 1, fontSize: 12, color: "#a2a2aa" }}>
                        ^  Superscript, _  Subscript, int  Integral, sum  Summation, prod  Product, sqrt  Square root, bar 
                        Bar over letter, alpha, beta, ... omega  Small Greek letter, Alpha, Beta, ... Omega  Capital Greek letter
                    </Text>
                </View>
            </View>
        ) : null;
    };

    const renderCueTabs = () => {
        return (
            <View style={{ flexDirection: "row" }}>
                <TouchableOpacity
                    style={{
                        justifyContent: "center",
                        flexDirection: "column"
                    }}
                    onPress={() => {
                        props.setShowOriginal(true);
                    }}>
                    <Text style={props.showOriginal ? styles.allGrayFill : styles.all}>
                        {PreferredLanguageText("viewShared")}
                    </Text>
                </TouchableOpacity>
                {(isOwner && submission) || isQuiz ? null : (
                    <TouchableOpacity
                        style={{
                            justifyContent: "center",
                            flexDirection: "column"
                        }}
                        onPress={() => {
                            props.setShowOriginal(false);
                        }}>
                        <Text style={!props.showOriginal && !props.viewStatus ? styles.allGrayFill : styles.all}>
                            {submission ? PreferredLanguageText("mySubmission") : PreferredLanguageText("myNotes")}
                        </Text>
                    </TouchableOpacity>
                )}
                {/* Add Status button here */}
                {!isOwner ? null : (
                    <TouchableOpacity
                        style={{
                            justifyContent: "center",
                            flexDirection: "column"
                        }}
                        onPress={() => {
                            props.setShowOriginal(false);
                            setIsQuiz(false);
                            props.changeViewStatus();
                        }}>
                        <Text style={props.viewStatus ? styles.allGrayFill : styles.all}>Status</Text>
                    </TouchableOpacity>
                )}
            </View>
        );
    };

    // QUIZ TIMER OR DOWNLOAD/REFRESH IF UPLOADED
    const renderQuizTimerOrUploadOptions = () => {
        return props.showOriginal && (imported || isQuiz) ? (
            <View style={{ flexDirection: "row", marginRight: 0, marginLeft: 0 }}>
                <View style={{ width: "40%", alignSelf: "flex-start" }}>
                    <TextInput
                        editable={isOwner}
                        value={title}
                        style={styles.input}
                        placeholder={"Title"}
                        onChangeText={val => setTitle(val)}
                        placeholderTextColor={"#a2a2aa"}
                    />
                </View>
                {isQuiz && !props.cue.graded ? (
                    isQuizTimed && (!props.cue.submittedAt || props.cue.submittedAt !== "") ? (
                        initiatedAt && initDuration !== 0 ? (
                            <View
                                style={{
                                    flex: 1,
                                    flexDirection: "row",
                                    justifyContent: "flex-end"
                                }}>
                                <CountdownCircleTimer
                                    size={120}
                                    key={initDuration}
                                    children={({ remainingTime }: any) => {
                                        if (!remainingTime || remainingTime === 0) {
                                            handleSubmit();
                                        }
                                        const hours = Math.floor(remainingTime / 3600);
                                        const minutes = Math.floor((remainingTime % 3600) / 60);
                                        const seconds = remainingTime % 60;
                                        return `${hours}h ${minutes}m ${seconds}s`;
                                    }}
                                    isPlaying={true}
                                    duration={duration}
                                    initialRemainingTime={initDuration}
                                    colors="#3B64F8"
                                />
                            </View>
                        ) : null
                    ) : null
                ) : (imported &&
                    (type === "mp4" ||
                        type === "mp3" ||
                        type === "mov" ||
                        type === "mpeg" ||
                        type === "mp2" ||
                        type === "wav")) ||
                    props.cue.graded ? null : (
                    <View
                        style={{
                            marginLeft: 25,
                            marginTop: 20,
                            alignSelf: "flex-start",
                            display: "flex",
                            flexDirection: "row"
                        }}>
                        {/* <View style={{ marginRight: 25 }}>
                            <View
                                style={{
                                    flexDirection: "row",
                                    justifyContent: "center",
                                    flex: 1
                                }}>
                                <Ionicons
                                    name="reload-outline"
                                    color="#a2a2aa"
                                    size={20}
                                    onPress={() => setWebviewKey(Math.random())}
                                />
                            </View>
                            <Text
                                style={{
                                    fontSize: 9,
                                    color: "#a2a2aa",
                                    textAlign: "center"
                                }}>
                                Reload
                            </Text>
                        </View> */}
                        <a download={true} href={url} style={{ textDecoration: "none", textAlign: "center" }}>
                            <View>
                                <Ionicons name="cloud-download-outline" color="#a2a2aa" size={20} />
                                <Text
                                    style={{
                                        fontSize: 9,
                                        color: "#a2a2aa",
                                        textAlign: "center"
                                    }}>
                                    Download
                                </Text>
                            </View>
                        </a>
                        {
                            isOwner ?
                                <TouchableOpacity
                                    style={{
                                        marginLeft: 15
                                    }}
                                    onPress={() => clearAll()}
                                >
                                    <Ionicons name="trash-outline" color="#a2a2aa" size={20} style={{ alignSelf: 'center' }} />
                                    <Text
                                        style={{
                                            fontSize: 9,
                                            color: "#a2a2aa",
                                            textAlign: "center"
                                        }}>
                                        Remove
                                    </Text>
                                </TouchableOpacity> : null
                        }
                    </View>
                )}
            </View>
        ) : null;
    };

    const renderCueRemarks = () => {
        return !props.showOriginal && props.cue.graded && props.cue.comment ? (
            <View>
                <Text
                    style={{
                        color: "#202025",
                        fontSize: 14,
                        paddingBottom: 25,
                        marginLeft: "5%"
                    }}>
                    {PreferredLanguageText("gradersRemarks")}
                </Text>
                <TextInput
                    value={props.cue.comment}
                    style={{
                        height: 200,
                        backgroundColor: "#f4f4f6",
                        borderRadius: 10,
                        fontSize: 15,
                        padding: 15,
                        paddingTop: 13,
                        paddingBottom: 13,
                        marginTop: 5,
                        marginBottom: 20
                    }}
                    editable={false}
                    placeholder={"Optional"}
                    placeholderTextColor={"#a2a2aa"}
                    multiline={true}
                />
            </View>
        ) : null;
    };

    const renderMainCueContent = () => (
        <View
            style={{
                width: "100%",
                minHeight: 475,
                backgroundColor: "white"
            }}>
            {!props.showOriginal ? null : isQuiz ? (
                isQuizTimed && !isOwner ? (
                    initiatedAt ? (
                        <Quiz
                            // disable quiz if graded or deadline has passed
                            submitted={isQuiz && props.cue.submittedAt && props.cue.submittedAt !== "" ? true : false}
                            graded={props.cue.graded}
                            hasEnded={currentDate >= deadline}
                            solutions={solutions}
                            problems={problems}
                            setSolutions={(s: any) => setSolutions(s)}
                            shuffleQuiz={shuffleQuiz}
                        />
                    ) : (
                        <View>
                            <View>
                                <TouchableOpacity
                                    onPress={() => initQuiz()}
                                    style={{
                                        backgroundColor: "white",
                                        overflow: "hidden",
                                        height: 35,
                                        marginTop: 15,
                                        justifyContent: "center",
                                        flexDirection: "row",
                                        marginBottom: 50
                                    }}>
                                    <Text
                                        style={{
                                            textAlign: "center",
                                            lineHeight: 35,
                                            color: "#202025",
                                            fontSize: 12,
                                            backgroundColor: "#f4f4f6",
                                            paddingHorizontal: 25,
                                            fontFamily: "inter",
                                            height: 35,
                                            width: 200,
                                            borderRadius: 15,
                                            textTransform: "uppercase"
                                        }}>
                                        {PreferredLanguageText("startQuiz")}
                                    </Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    )
                ) : (
                    <Quiz
                        isOwner={isOwner}
                        submitted={isQuiz && props.cue.submittedAt && props.cue.submittedAt !== "" ? true : false}
                        graded={props.cue.graded || currentDate >= deadline}
                        solutions={solutions}
                        problems={problems}
                        setSolutions={(s: any) => setSolutions(s)}
                        shuffleQuiz={shuffleQuiz}
                    />
                )
            ) : imported ? (
                type === "mp4" || type === "mp3" || type === "mov" || type === "mpeg" || type === "mp2" || type === "wav" ? (
                    <ReactPlayer
                        url={url}
                        controls={true}
                        onContextMenu={(e: any) => e.preventDefault()}
                        config={{
                            file: { attributes: { controlsList: "nodownload" } }
                        }}
                    />
                ) : (
                    <View
                        key={url}
                        style={{ flex: 1 }}>
                        <Webview
                            url={url}
                            key={url}
                        />
                    </View>
                )
            ) : (
                renderRichEditorOriginalCue()
            )}
            {props.showOriginal ? null : submissionImported ? (
                submissionType === "mp4" ||
                    submissionType === "mp3" ||
                    submissionType === "mov" ||
                    submissionType === "mpeg" ||
                    submissionType === "mp2" ||
                    submissionType === "wav" ? (
                    <ReactPlayer url={submissionUrl} controls={true} />
                ) : (
                    <View
                        key={submissionUrl}
                        style={{ flex: 1 }}>
                        <Webview
                            key={submissionUrl}
                            url={submissionUrl}
                        />
                    </View>
                )
            ) : (
                renderRichEditorModified()
            )}
        </View>
    );

    const renderRichEditorOriginalCue = () => (
        <RichEditor
            key={props.showOriginal.toString() + reloadEditorKey.toString()}
            disabled={!isOwner}
            containerStyle={{
                height: height,
                backgroundColor: "#f4f4f6",
                padding: 3,
                paddingTop: 5,
                paddingBottom: 10,
                borderRadius: 15
            }}
            ref={RichText}
            style={{
                width: "100%",
                backgroundColor: "#f4f4f6",
                minHeight: 475,
                borderRadius: 15
            }}
            editorStyle={{
                backgroundColor: "#f4f4f6",
                placeholderColor: "#a2a2aa",
                color: "#202025",
                contentCSSText: "font-size: 13px;"
            }}
            initialContentHTML={original}
            onScroll={() => Keyboard.dismiss()}
            placeholder={"Title"}
            onChange={text => {
                const modifedText = text.split("&amp;").join("&");
                setOriginal(modifedText);
            }}
            onHeightChange={handleHeightChange}
            onBlur={() => Keyboard.dismiss()}
            allowFileAccess={true}
            allowFileAccessFromFileURLs={true}
            allowUniversalAccessFromFileURLs={true}
            allowsFullscreenVideo={true}
            allowsInlineMediaPlayback={true}
            allowsLinkPreview={true}
            allowsBackForwardNavigationGestures={true}
        />
    );

    const renderRichEditorModified = () => (
        <RichEditor
            key={props.showOriginal.toString() + reloadEditorKey.toString()}
            containerStyle={{
                height: height,
                backgroundColor: "#f4f4f6",
                padding: 3,
                paddingTop: 5,
                paddingBottom: 10,
                borderRadius: 15
            }}
            disabled={(props.cue.graded && submission) || (currentDate > deadline && submission)}
            ref={RichText}
            style={{
                width: "100%",
                backgroundColor: "#f4f4f6",
                minHeight: 475,
                borderRadius: 15
            }}
            editorStyle={{
                backgroundColor: "#f4f4f6",
                placeholderColor: "#a2a2aa",
                color: "#202025",
                contentCSSText: "font-size: 13px;"
            }}
            initialContentHTML={cue}
            onScroll={() => Keyboard.dismiss()}
            placeholder={props.cue.channelId && props.cue.channelId !== '' ? (submission ? "Submission" : "Notes") : "Title"}
            onChange={text => {
                const modifedText = text.split("&amp;").join("&");
                setCue(modifedText);
            }}
            onHeightChange={handleHeightChange}
            onBlur={() => Keyboard.dismiss()}
            allowFileAccess={true}
            allowFileAccessFromFileURLs={true}
            allowUniversalAccessFromFileURLs={true}
            allowsFullscreenVideo={true}
            allowsInlineMediaPlayback={true}
            allowsLinkPreview={true}
            allowsBackForwardNavigationGestures={true}
        />
    );

    const renderShareWithOptions = () => {
        return props.cue.channelId !== "" && isOwner ? (
            <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>
                        {props.cue.channelId && props.cue.channelId !== "" ? "Shared with" : "Saved in"}
                    </Text>
                </View>
                <View
                    style={{
                        maxHeight: 175,
                        flexDirection: "column",
                        overflow: "scroll"
                    }}>
                    <View
                        style={{
                            width: "90%",
                            padding: 5,
                            height: expandMenu ? 175 : "auto"
                        }}>
                        <Select
                            isClearable={false}
                            placeholder="Share with"
                            styles={{
                                menu: (provided: any, state: any) => ({
                                    ...provided,
                                    zIndex: 9999,
                                    overflow: "scroll",
                                    height: 125,
                                    display: "flex",
                                    margin: 5,
                                    width: "97%",
                                    boxShadow: "none"
                                }),
                                option: (provided: any, state: any) => ({
                                    ...provided,
                                    fontFamily: "overpass",
                                    color: "#a2a2aa",
                                    fontSize: 10,
                                    height: 25,
                                    width: "97%"
                                }),
                                input: (styles: any) => ({
                                    // ...styles,
                                    width: "100%",
                                    border: "none",
                                    borderWidth: 0,
                                    fontSize: 12
                                }),
                                placeholder: (styles: any) => ({
                                    ...styles,
                                    fontFamily: "overpass",
                                    color: "#a2a2aa",
                                    fontSize: 12
                                }),
                                multiValueLabel: (styles: any, { data }: any) => ({
                                    ...styles,
                                    color: "#202025",
                                    fontFamily: "overpass"
                                }),
                                multiValue: (styles: any, { data }: any) => ({
                                    ...styles,
                                    backgroundColor: "#f4f4f6",
                                    fontFamily: "overpass"
                                }),
                                multiValueRemove: (base: any, state: any) => {
                                    return state.data.isFixed ? { ...base, display: "none" } : base;
                                }
                            }}
                            value={selected}
                            isMulti={true}
                            onMenuOpen={() => setExpandMenu(true)}
                            onMenuClose={() => setExpandMenu(false)}
                            name="Share with"
                            className="basic-multi-select"
                            classNamePrefix="select"
                            onChange={onChange}
                            options={subscribers}
                        />
                    </View>
                </View>
            </View>
        ) : null;
    };

    const renderSubmissionRequiredOptions = () => {
        return props.cue.channelId !== "" ? (
            <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>{PreferredLanguageText("submissionRequired")}</Text>
                </View>
                <View style={{ flexDirection: "row", width: '100%' }}>
                    {isOwner ? (
                        <View
                            style={{
                                backgroundColor: "white",
                                height: 40,
                                marginRight: 10
                            }}>
                            <Switch
                                disabled={isQuiz}
                                value={submission}
                                onValueChange={() => {
                                    setSubmission(!submission);
                                }}
                                style={{ height: 20 }}
                                trackColor={{
                                    false: "#f4f4f6",
                                    true: "#a2a2aa"
                                }}
                                activeThumbColor="white"
                            />
                        </View>
                    ) : (
                        <View style={{ flex: 1, backgroundColor: "#fff" }}>
                            <Text style={{ fontSize: 12, color: "#a2a2aa" }}>{!submission ? PreferredLanguageText("no") : null}</Text>
                        </View>
                    )}
                    {submission ? (
                        <View
                            style={{
                                width: "100%",
                                display: "flex",
                                flexDirection: "row",
                                backgroundColor: "white",
                                marginBottom: 10
                            }}>
                            <Text
                                style={{
                                    fontSize: 12,
                                    color: "#a2a2aa",
                                    textAlign: "left",
                                    paddingRight: 10
                                }}>
                                Available
                            </Text>
                            {isOwner ? (
                                <Datetime
                                    value={initiateAt}
                                    onChange={(event: any) => {
                                        const date = new Date(event);

                                        if (date < new Date()) return;
                                        setInitiateAt(date);
                                    }}
                                    isValidDate={disablePastDt}
                                />
                            ) : (
                                <Text
                                    style={{
                                        fontSize: 12,
                                        color: "#a2a2aa",
                                        textAlign: "left"
                                    }}>
                                    {initiateAt.toLocaleString()}
                                </Text>
                            )}
                        </View>
                    ) : null}

                </View>
                {submission ? (
                    <View
                        style={{
                            width: "100%",
                            display: "flex",
                            flexDirection: "row",
                            backgroundColor: "white",
                            marginLeft: isOwner ? 50 : 0
                        }}>
                        <Text
                            style={{
                                fontSize: 12,
                                    color: "#a2a2aa",
                                    textAlign: "left",
                                    paddingRight: 10
                                }}>
                                Deadline
                            </Text>
                        {isOwner ? (
                            <Datetime
                                value={deadline}
                                onChange={(event: any) => {
                                    const date = new Date(event);

                                    if (date < new Date()) return;
                                    setDeadline(date);
                                }}
                                isValidDate={disablePastDt}
                            />
                        ) : (
                            <Text
                                    style={{
                                        fontSize: 12,
                                        color: "#a2a2aa",
                                        textAlign: "left"
                                    }}>
                                    {deadline.toLocaleString()}
                                </Text>
                        )}
                    </View>
                ) : null}
            </View>
        ) : null;
    };

    const renderGradeOptions = () => {
        return submission ? (
            <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>Grade Weight</Text>
                </View>
                <View style={{ flexDirection: "row" }}>
                    <View
                        style={{
                            backgroundColor: "white",
                            height: 40,
                            marginRight: 10
                        }}>
                        <Switch
                            disabled={!isOwner}
                            value={graded}
                            onValueChange={() => setGraded(!graded)}
                            style={{ height: 20 }}
                            trackColor={{
                                false: "#f4f4f6",
                                true: "#a2a2aa"
                            }}
                            activeThumbColor="white"
                        />
                    </View>
                    {graded ? (
                        <View
                            style={{
                                width: "100%",
                                display: "flex",
                                flexDirection: "row",
                                backgroundColor: "white"
                            }}>
                            <Text
                                style={{
                                    fontSize: 12,
                                    color: "#a2a2aa",
                                    textAlign: "left",
                                    paddingRight: 10,
                                    paddingLeft: 10,
                                }}>
                                {PreferredLanguageText("percentageOverall")}
                            </Text>
                            {isOwner ? (
                                <TextInput
                                    value={gradeWeight}
                                    style={styles.picker}
                                    placeholder={"0-100"}
                                    onChangeText={val => setGradeWeight(val)}
                                    placeholderTextColor={"#a2a2aa"}
                                />
                            ) : (
                                <Text
                                    style={{
                                        color: "#a2a2aa",
                                        textAlign: "left",
                                        fontSize: 12
                                    }}>
                                    {gradeWeight}
                                </Text>
                            )}
                        </View>
                    ) : null}
                </View>
            </View>
        ) : null;
    };

    const renderCategoryOptions = () => {
        return (props.cue.channelId && props.cue.channelId !== "" && isOwner) ||
            !props.channelId ||
            props.channelId === "" ? (
            <View
                style={{
                    width: width < 768 ? "100%" : "33.33%",
                    borderRightWidth: 0,
                    borderColor: "#f4f4f6"
                }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>{PreferredLanguageText("category")}</Text>
                </View>
                {props.cue.channelId && !props.channelOwner ? (
                    <View
                        style={{
                            width: "100%",
                            display: "flex",
                            flexDirection: "row",
                            backgroundColor: "white"
                        }}>
                        <View style={{ width: "85%", backgroundColor: "white" }}>
                            <View style={styles.colorBar}>
                                <TouchableOpacity style={styles.allGrayOutline} onPress={() => { }}>
                                    <Text
                                        style={{
                                            color: "#a2a2aa",
                                            lineHeight: 20,
                                            fontSize: 12
                                        }}>
                                        {props.cue.customCategory === "" ? PreferredLanguageText("none") : props.cue.customCategory}
                                    </Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
                ) : (
                    <View
                        style={{
                            width: "100%",
                            display: "flex",
                            flexDirection: "row",
                            backgroundColor: "white"
                        }}>
                        <View style={{ width: "85%", backgroundColor: "white" }}>
                            {addCustomCategory ? (
                                <View style={styles.colorBar}>
                                    <TextInput
                                        value={customCategory}
                                        style={styles.allGrayOutline}
                                        placeholder={"Enter Category"}
                                        onChangeText={val => {
                                            setCustomCategory(val);
                                        }}
                                        placeholderTextColor={"#a2a2aa"}
                                    />
                                </View>
                            ) : (
                                <ScrollView style={styles.colorBar} horizontal={true} showsHorizontalScrollIndicator={false}>
                                    <TouchableOpacity
                                        style={customCategory === "" ? styles.allGrayOutline : styles.all}
                                        onPress={() => {
                                            setCustomCategory("");
                                        }}>
                                        <Text
                                            style={{
                                                color: "#a2a2aa",
                                                lineHeight: 20,
                                                fontSize: 12
                                            }}>
                                            {PreferredLanguageText("none")}
                                        </Text>
                                    </TouchableOpacity>
                                    {customCategories.map((category: string) => {
                                        return (
                                            <TouchableOpacity
                                                key={Math.random()}
                                                style={customCategory === category ? styles.allGrayOutline : styles.all}
                                                onPress={() => {
                                                    setCustomCategory(category);
                                                }}>
                                                <Text
                                                    style={{
                                                        color: "#a2a2aa",
                                                        lineHeight: 20,
                                                        fontSize: 12
                                                    }}>
                                                    {category}
                                                </Text>
                                            </TouchableOpacity>
                                        );
                                    })}
                                </ScrollView>
                            )}
                        </View>
                        <View style={{ width: "15%", backgroundColor: "white" }}>
                            <TouchableOpacity
                                onPress={() => {
                                    if (addCustomCategory) {
                                        setCustomCategory("");
                                        setAddCustomCategory(false);
                                    } else {
                                        setCustomCategory("");
                                        setAddCustomCategory(true);
                                    }
                                }}
                                style={{ backgroundColor: "white" }}>
                                <Text
                                    style={{
                                        textAlign: "right",
                                        lineHeight: 20,
                                        width: "100%"
                                    }}>
                                    <Ionicons name={addCustomCategory ? "close" : "add"} size={20} color={"#a2a2aa"} />
                                </Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                )}
            </View>
        ) : null;
    };

    const renderPriorityOptions = () => {
        return (
            <View
                style={{
                    width: width < 768 ? "100%" : "33.33%",
                    borderRightWidth: 0,
                    borderColor: "#f4f4f6"
                }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>{PreferredLanguageText("priority")}</Text>
                </View>
                <View
                    style={{
                        width: "100%",
                        display: "flex",
                        flexDirection: "row",
                        backgroundColor: "white"
                    }}>
                    <View style={{ width: "100%", backgroundColor: "white" }}>
                        <ScrollView
                            style={{ ...styles.colorBar, height: 20 }}
                            horizontal={true}
                            showsHorizontalScrollIndicator={false}>
                            {colorChoices.map((c: string, i: number) => {
                                return (
                                    <View style={color == i ? styles.colorContainerOutline : styles.colorContainer} key={Math.random()}>
                                        <TouchableOpacity
                                            style={{
                                                width: 12,
                                                height: 12,
                                                borderRadius: 6,
                                                backgroundColor: colorChoices[i]
                                            }}
                                            onPress={() => {
                                                setColor(i);
                                            }}
                                        />
                                    </View>
                                );
                            })}
                        </ScrollView>
                    </View>
                </View>
            </View>
        );
    };

    const renderForwardOptions = () => {
        return channels.length === 0 || !isOwner ? null : (
            <View
                style={{
                    width: width < 768 ? "100%" : "33.33%",
                    borderRightWidth: 0,
                    borderColor: "#f4f4f6"
                }}>
                <View
                    style={{
                        width: "100%",
                        paddingTop: 40,
                        paddingBottom: 15,
                        backgroundColor: "white"
                    }}>
                    <Text style={{ fontSize: 12, color: "#a2a2aa" }}>Forward</Text>
                </View>
                <View
                    style={{
                        width: "100%",
                        display: "flex",
                        flexDirection: "row",
                        backgroundColor: "white"
                    }}>
                    <View style={{ width: "85%", backgroundColor: "white" }}>
                        <ScrollView style={styles.colorBar} horizontal={true} showsHorizontalScrollIndicator={false}>
                            {channels.map(channel => {
                                return (
                                    <TouchableOpacity
                                        key={Math.random()}
                                        style={shareWithChannelId === channel._id ? styles.allOutline : styles.allBlack}
                                        onPress={() => {
                                            if (shareWithChannelId === "") {
                                                setShareWithChannelId(channel._id);
                                            } else {
                                                setShareWithChannelId("");
                                            }
                                        }}>
                                        <Text
                                            style={{
                                                lineHeight: 20,
                                                fontSize: 12,
                                                color: shareWithChannelId === channel._id ? "#fff" : "#202025"
                                            }}>
                                            {channel.name}
                                        </Text>
                                    </TouchableOpacity>
                                );
                            })}
                        </ScrollView>
                    </View>
                    <View style={{ width: "15%", backgroundColor: "white" }}>
                        <TouchableOpacity
                            disabled={shareWithChannelId === ""}
                            onPress={() => shareCue()}
                            style={{ backgroundColor: "white" }}>
                            <Text
                                style={{
                                    textAlign: "center",
                                    lineHeight: 20,
                                    width: "100%"
                                }}>
                                <Ionicons
                                    name={"arrow-redo-outline"}
                                    size={20}
                                    color={shareWithChannelId === "" ? "#a2a2aa" : "#202025"}
                                />
                            </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        );
    };

    const renderReminderOptions = () => {
        return (
            <View
                style={{
                    width: "100%",
                    paddingTop: 15,
                    flexDirection: width < 768 ? "column" : "row"
                }}>
                <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                    <View
                        style={{
                            width: "100%",
                            paddingTop: 40,
                            paddingBottom: 15,
                            backgroundColor: "white"
                        }}>
                        <Text style={{ fontSize: 12, color: "#a2a2aa" }}>Reminder</Text>
                    </View>
                    <View
                        style={{
                            backgroundColor: "white",
                            width: "100%",
                            height: 40,
                            marginHorizontal: 10
                        }}>
                        <Switch
                            value={notify}
                            onValueChange={() => {
                                if (notify) {
                                    // setShuffle(false)
                                    setFrequency("0");
                                } else {
                                    // setShuffle(true)
                                    setFrequency("1-D");
                                }
                                setPlayChannelCueIndef(true);
                                setNotify(!notify);
                            }}
                            style={{ height: 20 }}
                            trackColor={{
                                false: "#f4f4f6",
                                true: "#3B64F8"
                            }}
                            activeThumbColor="white"
                        />
                    </View>
                </View>
                {notify ? (
                    <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                        <View
                            style={{
                                width: "100%",
                                paddingTop: 40,
                                paddingBottom: 15,
                                backgroundColor: "white"
                            }}>
                            <Text style={{ fontSize: 12, color: "#a2a2aa" }}>Recurring</Text>
                        </View>
                        <View style={{ flexDirection: "row" }}>
                            <View
                                style={{
                                    backgroundColor: "white",
                                    height: 40,
                                    marginHorizontal: 10
                                }}>
                                <Switch
                                    value={!shuffle}
                                    onValueChange={() => setShuffle(!shuffle)}
                                    style={{ height: 20 }}
                                    trackColor={{
                                        false: "#f4f4f6",
                                        true: "#a2a2aa"
                                    }}
                                    activeThumbColor="white"
                                />
                            </View>
                            {!shuffle ? (
                                <View
                                    style={{
                                        display: "flex",
                                        flexDirection: "row",
                                        backgroundColor: "white"
                                    }}>
                                    <Text style={styles.text}>{PreferredLanguageText("remindEvery")}</Text>
                                    <Picker
                                        style={styles.picker}
                                        itemStyle={{
                                            fontSize: 15
                                        }}
                                        selectedValue={frequency}
                                        onValueChange={(itemValue: any) => setFrequency(itemValue)}>
                                        {timedFrequencyOptions.map((item: any, index: number) => {
                                            return (
                                                <Picker.Item
                                                    color={frequency === item.value ? "#3B64F8" : "#202025"}
                                                    label={item.value === "0" && cue.channelId !== "" ? "Once" : item.label}
                                                    value={item.value}
                                                    key={index}
                                                />
                                            );
                                        })}
                                    </Picker>
                                </View>
                            ) : (
                                <View
                                    style={{
                                        width: "100%",
                                        display: "flex",
                                        flexDirection: "row",
                                        backgroundColor: "white"
                                    }}>
                                    <Text style={styles.text}>{PreferredLanguageText("remindOn")}</Text>
                                    <Datetime
                                        value={endPlayAt}
                                        onChange={(event: any) => {
                                            const date = new Date(event);
                                            if (date < new Date()) return;

                                            setEndPlayAt(date);
                                        }}
                                        isValidDate={disablePastDt}
                                    />
                                </View>
                            )}
                        </View>
                    </View>
                ) : null}
                {notify && !shuffle ? (
                    <View style={{ width: width < 768 ? "100%" : "33.33%" }}>
                        <View
                            style={{
                                width: "100%",
                                paddingTop: 40,
                                paddingBottom: 15,
                                backgroundColor: "white"
                            }}>
                            <Text style={{ fontSize: 12, color: "#a2a2aa" }}>Indefinite</Text>
                        </View>
                        <View style={{ flexDirection: "row" }}>
                            <View
                                style={{
                                    backgroundColor: "white",
                                    height: 40,
                                    marginHorizontal: 10
                                }}>
                                <Switch
                                    value={playChannelCueIndef}
                                    onValueChange={() => setPlayChannelCueIndef(!playChannelCueIndef)}
                                    style={{ height: 20 }}
                                    trackColor={{
                                        false: "#f4f4f6",
                                        true: "#a2a2aa"
                                    }}
                                    activeThumbColor="white"
                                />
                            </View>
                            {playChannelCueIndef ? null : (
                                <View
                                    style={{
                                        width: "100%",
                                        display: "flex",
                                        flexDirection: "row",
                                        backgroundColor: "white"
                                    }}>
                                    <Text style={styles.text}>{PreferredLanguageText("remindTill")}</Text>
                                    <Datetime
                                        onChange={(event: any) => {
                                            const date = new Date(event);
                                            if (date < new Date()) return;

                                            setEndPlayAt(date);
                                        }}
                                        value={endPlayAt}
                                        isValidDate={disablePastDt}
                                    />
                                </View>
                            )}
                        </View>
                    </View>
                ) : null}
            </View>
        );
    };

    const renderFooter = () => {
        return (
            <View style={styles.footer}>
                <View
                    style={{
                        flex: 1,
                        backgroundColor: "white",
                        justifyContent: "center",
                        display: "flex",
                        flexDirection: "row",
                        height: 50,
                        paddingTop: 10
                    }}>
                    {isOwner || !props.cue.channelId || props.cue.channelId === "" ? (
                        <TouchableOpacity onPress={() => handleDelete()} style={{ backgroundColor: "white", borderRadius: 15 }}>
                            <Text
                                style={{
                                    textAlign: "center",
                                    lineHeight: 35,
                                    color: "white",
                                    fontSize: 12,
                                    backgroundColor: "#3B64F8",
                                    borderRadius: 15,
                                    paddingHorizontal: 25,
                                    fontFamily: "inter",
                                    overflow: "hidden",
                                    height: 35,
                                    textTransform: "uppercase"
                                }}>
                                {isOwner
                                    ? props.cue.channelId && props.cue.channelId !== ""
                                        ? PreferredLanguageText("deleteForEveryone")
                                        : PreferredLanguageText("delete")
                                    : PreferredLanguageText("delete")}
                            </Text>
                        </TouchableOpacity>
                    ) : null}
                    {!isOwner && props.cue.channelId && props.cue.channelId !== "" && submission ? (
                        <TouchableOpacity
                            disabled={
                                // if user has not signed up
                                !userSetupComplete ||
                                // deadline has passed & its not an initiated timed quiz
                                (currentDate >= deadline && !(isQuiz && isQuizTimed && initiatedAt)) ||
                                // graded
                                props.cue.graded ||
                                // if timed quiz not initiated
                                (isQuiz && isQuizTimed && !initiatedAt) ||
                                // if quiz submitted already
                                (isQuiz && props.cue.submittedAt && props.cue.submittedAt !== "")
                            }
                            onPress={() => handleSubmit()}
                            style={{ backgroundColor: "white", borderRadius: 15 }}>
                            <Text
                                style={{
                                    textAlign: "center",
                                    lineHeight: 35,
                                    color: "white",
                                    fontSize: 12,
                                    backgroundColor: "#3B64F8",
                                    borderRadius: 15,
                                    paddingHorizontal: 25,
                                    fontFamily: "inter",
                                    overflow: "hidden",
                                    height: 35,
                                    textTransform: 'uppercase'
                                }}>
                                {userSetupComplete
                                    ? (props.cue.submittedAt && props.cue.submittedAt !== "") || submitted
                                        ? props.cue.graded
                                            ? PreferredLanguageText("graded")
                                            : isQuiz
                                                ? PreferredLanguageText("submitted")
                                                : currentDate < deadline
                                                    ? PreferredLanguageText("resubmit")
                                                    : PreferredLanguageText("submissionEnded")
                                        : currentDate < deadline
                                            ? PreferredLanguageText("submit")
                                            : PreferredLanguageText("submissionEnded")
                                    : PreferredLanguageText("signupToSubmit")}
                            </Text>
                        </TouchableOpacity>
                    ) : null}
                </View>
            </View>
        );
    };



    if (initiateAt > new Date() && !isOwner) {
        return (<View style={{ minHeight: Dimensions.get('window').height }}>
            <View style={{ backgroundColor: 'white', flex: 1, }}>
                <Text style={{ width: '100%', color: '#a2a2aa', fontSize: 22, paddingTop: 200, paddingBottom: 100, paddingHorizontal: 5, fontFamily: 'inter', flex: 1, textAlign: 'center' }}>
                    Available from {moment(initiateAt).format('MMMM Do YYYY, h:mm a')}
                </Text>
            </View>
        </View>)
    }

    // MAIN RETURN
    return (
        <View
            style={{
                width: "100%",
                // height: Dimensions.get('window').height - 30,
                backgroundColor: "white",
                borderTopLeftRadius: 0,
                borderTopRightRadius: 0,
                paddingHorizontal: 20
                // overflow: 'hidden'
            }}>
            <Animated.View
                style={{
                    width: "100%",
                    backgroundColor: "white",
                    opacity: 1,
                    borderTopLeftRadius: 0,
                    borderTopRightRadius: 0
                    // height: '100%'
                }}>
                <Text
                    style={{
                        width: "100%",
                        textAlign: "center",
                        height: 15,
                        paddingBottom: 30
                    }}>
                    {/* <Ionicons name='chevron-down' size={20} color={'#e0e0e0'} /> */}
                </Text>

                {props.cue.channelId && props.cue.channelId !== "" ? (
                    <View
                        style={{
                            width: "100%",
                            flexDirection: "row",
                            marginBottom: 5
                        }}>
                        {renderCueTabs()}
                        {props.cue.graded && props.cue.score !== undefined && props.cue.score !== null ? (
                            <Text
                                style={{
                                    fontSize: 12,
                                    color: "white",
                                    height: 22,
                                    paddingHorizontal: 10,
                                    marginLeft: 10,
                                    borderRadius: 10,
                                    backgroundColor: "#3B64F8",
                                    lineHeight: 20,
                                    paddingTop: 1
                                }}>
                                {props.cue.score}%
                            </Text>
                        ) : null}
                        <TouchableOpacity
                            onPress={() => setStarred(!starred)}
                            style={{
                                backgroundColor: "white",
                                flex: 1
                            }}>
                            <Text
                                style={{
                                    textAlign: "right",
                                    lineHeight: 30,
                                    marginTop: -31,
                                    // paddingRight: 25,
                                    width: "100%"
                                }}>
                                <Ionicons name="bookmark" size={34} color={starred ? "#d91d56" : "#a2a2aa"} />
                            </Text>
                        </TouchableOpacity>
                    </View>
                ) : (
                    <View style={{ flexDirection: "row" }}>
                        <View style={{ backgroundColor: "white", flex: 1 }}>
                            <Text
                                style={{
                                    fontSize: 11,
                                    paddingBottom: 20,
                                    textTransform: "uppercase",
                                    // paddingLeft: 10
                                }}>
                                {PreferredLanguageText("update")}
                            </Text>
                        </View>
                        <TouchableOpacity
                            onPress={() => setStarred(!starred)}
                            style={{
                                backgroundColor: "white",
                                flex: 1
                            }}>
                            <Text
                                style={{
                                    textAlign: "right",
                                    lineHeight: 30,
                                    marginTop: -31,
                                    // paddingRight: 25,
                                    width: "100%"
                                }}>
                                <Ionicons name="bookmark" size={34} color={starred ? "#d91d56" : "#a2a2aa"} />
                            </Text>
                        </TouchableOpacity>
                    </View>
                )}
                <View
                    style={{
                        width: "100%",
                        display: "flex",
                        flexDirection: Dimensions.get("window").width < 768 ? "column-reverse" : "row",
                        paddingBottom: 4,
                        backgroundColor: "white",
                        marginTop: 20
                    }}
                    onTouchStart={() => Keyboard.dismiss()}>
                    <View
                        style={{
                            flexDirection: Dimensions.get("window").width < 768 ? "column" : "row",
                            flex: 1
                        }}>
                        {renderRichToolbar()}
                        {(!props.showOriginal && props.cue.submission && !submissionImported && showImportOptions)
                            || (props.showOriginal && showImportOptions && isOwner) ? (
                            <FileUpload
                                back={() => setShowImportOptions(false)}
                                onUpload={(u: any, t: any) => {
                                    const obj = { url: u, type: t, title: props.showOriginal ? title : submissionTitle };
                                    if (props.showOriginal) {
                                        setOriginal(JSON.stringify(obj))
                                    } else {
                                        setCue(JSON.stringify(obj))
                                    }
                                    setShowImportOptions(false);
                                }}
                            />
                        ) : null}
                    </View>
                    {!props.showOriginal && !submissionImported && !props.cue.graded ? (
                        <Text
                            style={{
                                color: "#a2a2aa",
                                fontSize: 11,
                                lineHeight: 30,
                                textAlign: "right",
                                paddingRight: 20,
                                textTransform: "uppercase"
                            }}
                            onPress={() => setShowEquationEditor(!showEquationEditor)}>
                            {showEquationEditor ? PreferredLanguageText("hide") : PreferredLanguageText("formula")}
                        </Text>
                    ) : null}
                    {!props.showOriginal &&
                        props.cue.submission &&
                        currentDate < deadline &&
                        !submissionImported &&
                        !showImportOptions &&
                        !props.cue.graded ? (
                        <Text
                            style={{
                                color: "#a2a2aa",
                                fontSize: 11,
                                lineHeight: 30,
                                textAlign: "right",
                                // paddingRight: 10,
                                textTransform: "uppercase"
                            }}
                            onPress={() => setShowImportOptions(true)}>
                            {PreferredLanguageText("import")} {Dimensions.get("window").width < 768 ? "" : "   "}
                        </Text>
                    ) : (
                        (props.showOriginal && !isOwner) || // viewing import as non import
                            (props.showOriginal && isOwner && imported) ||  // viewing import as owner
                            (!props.showOriginal && isOwner && (props.cue.channelId && props.cue.channelId !== '')) || // no submission as owner
                            (!props.showOriginal && submissionImported && !isOwner) ||  // submitted as non owner
                            (!props.showOriginal && !submission && (props.cue.channelId && props.cue.channelId !== ''))   // my notes
                            ? null :
                            (
                                <Text style={{
                                    color: '#a2a2aa',
                                    fontSize: 11,
                                    lineHeight: 30,
                                    textAlign: 'right',
                                    // paddingRight: 10,
                                    textTransform: 'uppercase'
                                }}
                                    onPress={() => setShowImportOptions(true)}
                                >
                                    {PreferredLanguageText('import')}     {Dimensions.get('window').width < 768 ? '' : '   '}
                                </Text>
                            )
                    )}
                </View>
                {renderEquationEditor()}
                <ScrollView
                    style={{
                        paddingBottom: 25,
                        height: "100%",
                        borderBottomColor: "#f4f4f6",
                        borderBottomWidth: 1
                    }}
                    showsVerticalScrollIndicator={false}
                    scrollEnabled={true}
                    scrollEventThrottle={1}
                    keyboardDismissMode={"on-drag"}
                    overScrollMode={"always"}
                    nestedScrollEnabled={true}>
                    {renderQuizTimerOrUploadOptions()}
                    {renderCueRemarks()}
                    {!props.showOriginal && submissionImported && !isQuiz ? (
                        <View style={{ flexDirection: "row" }}>
                            <View
                                style={{
                                    width: "40%",
                                    alignSelf: "flex-start",
                                    marginLeft: 0
                                }}>
                                <TextInput
                                    value={submissionTitle}
                                    style={styles.input}
                                    placeholder={"Title"}
                                    onChangeText={val => setSubmissionTitle(val)}
                                    placeholderTextColor={"#a2a2aa"}
                                />
                            </View>
                            {props.cue.submittedAt && props.cue.submittedAt !== "" ? (
                                <View
                                    style={{
                                        marginLeft: 25,
                                        marginTop: 20,
                                        alignSelf: "flex-start",
                                        display: "flex",
                                        flexDirection: "row"
                                    }}>
                                    {/* <View style={{ marginRight: 25 }}>
                                        <View
                                            style={{
                                                flexDirection: "row",
                                                justifyContent: "center",
                                                flex: 1
                                            }}>
                                            <Ionicons
                                                name="reload-outline"
                                                color="#a2a2aa"
                                                size={20}
                                                onPress={() => setWebviewKey(Math.random())}
                                            />
                                        </View>
                                        <Text
                                            style={{
                                                fontSize: 9,
                                                color: "#a2a2aa",
                                                textAlign: "center"
                                            }}>
                                            Reload
                                        </Text>
                                    </View> */}
                                    <a download={true} href={submissionUrl} style={{ textDecoration: "none", textAlign: "center" }}>
                                        <View>
                                            <Ionicons name="cloud-download-outline" color="#a2a2aa" size={20} style={{ alignSelf: 'center' }} />
                                            <Text
                                                style={{
                                                    fontSize: 9,
                                                    color: "#a2a2aa",
                                                    textAlign: "center"
                                                }}>
                                                Download
                                            </Text>
                                        </View>
                                    </a>
                                    {
                                        props.cue.graded || (currentDate > deadline) ? null :
                                            <TouchableOpacity
                                                onPress={() => clearAll()}
                                                style={{ marginLeft: 15, alignContent: 'center' }}
                                            >
                                                <Ionicons name="trash-outline" color="#a2a2aa" size={20} />
                                                <Text
                                                    style={{
                                                        fontSize: 9,
                                                        color: "#a2a2aa",
                                                        textAlign: "center"
                                                    }}>
                                                    Remove
                                                </Text>
                                            </TouchableOpacity>
                                    }
                                </View>
                            ) : <TouchableOpacity
                                style={{
                                    marginLeft: 15
                                }}
                                onPress={() => clearAll()}
                            >
                                <Ionicons name="trash-outline" color="#a2a2aa" size={20} style={{ alignSelf: 'center' }} />
                                <Text
                                    style={{
                                        fontSize: 9,
                                        color: "#a2a2aa",
                                        textAlign: "center"
                                    }}>
                                    Remove
                                </Text>
                            </TouchableOpacity>}
                        </View>
                    ) : null}
                    {submissionImported || imported ? (
                        // This is because the toolbar wont have an editor to connect with if the file is imported
                        <RichEditor
                            key={props.showOriginal.toString() + reloadEditorKey.toString()}
                            disabled={true}
                            containerStyle={{
                                display: "none"
                            }}
                            ref={RichText}
                            style={{
                                display: "none"
                            }}
                        />
                    ) : null}
                    {renderMainCueContent()}
                    <TouchableOpacity
                        onPress={() => setShowOptions(!showOptions)}
                        style={{
                            width: "100%",
                            flexDirection: "row",
                            // marginTop: 20,
                            // borderTopColor: "#f4f4f6",
                            // borderTopWidth: 1,
                            paddingTop: 40,
                            paddingBottom: 20
                        }}>
                        <Text style={{
                            lineHeight: 23,
                            marginRight: 10,
                            color: '#a2a2aa',
                            fontSize: 11,
                            textTransform: 'uppercase'
                        }}>
                            {PreferredLanguageText('options') + '       '}
                        </Text>
                        <Text style={{ lineHeight: 21 }}>
                            <Ionicons size={14} name={showOptions ? 'caret-down-outline' : 'caret-forward-outline'} color='#a2a2aa' />
                        </Text>
                    </TouchableOpacity>
                    <Collapse isOpened={showOptions}>
                        <View style={{ flex: 1, display: "flex", flexDirection: "column" }}>
                            {props.cue.channelId ? (
                                <View
                                    style={{
                                        display: "flex",
                                        flexDirection: width < 768 ? "column" : "row"
                                    }}>
                                    {renderShareWithOptions()}
                                    {renderSubmissionRequiredOptions()}
                                    {renderGradeOptions()}
                                </View>
                            ) : null}
                            <View
                                style={{
                                    display: "flex",
                                    flexDirection: width < 768 ? "column" : "row"
                                }}>
                                {renderCategoryOptions()}
                                {renderPriorityOptions()}
                                {renderForwardOptions()}
                            </View>
                        </View>
                        {renderReminderOptions()}
                        {isQuiz && isOwner ? <View style={{ width: width < 768 ? '100%' : '33.33%' }}>
                            <View style={{ width: '100%', paddingTop: 40, paddingBottom: 15, backgroundColor: 'white' }}>
                                <Text style={{ fontSize: 12, color: '#a2a2aa' }}>
                                    Shuffle Questions
                                </Text>
                            </View>
                            <View style={{ flexDirection: 'row' }}>
                                <View style={{
                                    backgroundColor: 'white',
                                    height: 40,
                                    marginRight: 10
                                }}>
                                    <Switch
                                        value={shuffleQuiz}
                                        disabled={true}
                                        onValueChange={() => setShuffleQuiz(!shuffleQuiz)}
                                        style={{ height: 20 }}
                                        trackColor={{
                                            false: '#f4f4f6',
                                            true: '#a2a2aa'
                                        }}
                                        activeThumbColor='white'
                                    />
                                </View>
                            </View>
                        </View> : null}
                        {renderFooter()}
                    </Collapse>
                </ScrollView>
            </Animated.View>
        </View>
    );
};

export default UpdateControls;

