import React, { useCallback, useEffect, useState } from "react";
import {
  ActivityIndicator,
  Animated,
  Dimensions,
  Switch,
  StyleSheet,
  ScrollView,
  View as DefaultView,
} from "react-native";
import { TextInput } from "./CustomTextInput";
import Alert from "./Alert";
import { Text, View, TouchableOpacity } from "./Themed";
import { fetchAPI } from "../graphql/FetchAPI";
import {
  createDate,
  deleteDate,
  getChannels,
  getEvents,
  createDateV1,
  editDateV1,
  deleteDateV1,
} from "../graphql/QueriesAndMutations";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Calendar, momentLocalizer } from "react-big-calendar";
import Datetime from "react-datetime";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { htmlStringParser } from "../helpers/HTMLParser";
import { Ionicons } from "@expo/vector-icons";
import { PreferredLanguageText } from "../helpers/LanguageContext";
import { Picker } from "@react-native-picker/picker";
import { eventFrequencyOptions } from "../helpers/FrequencyOptions";

// Try New Calendar
// import Scheduler, {SchedulerData, ViewTypes, DATE_FORMAT} from 'react-big-scheduler'
// import 'react-big-scheduler/lib/css/style.css';

const CalendarX: React.FunctionComponent<{ [label: string]: any }> = (
  props: any
) => {
  const [modalAnimation] = useState(new Animated.Value(1));
  const [loading, setLoading] = useState(true);
  const [events, setEvents] = useState<any[]>([]);

  const [title, setTitle] = useState("");
  const [start, setStart] = useState(new Date());
  const [end, setEnd] = useState(new Date(start.getTime() + 1000 * 60 * 60));
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);
  const [channels, setChannels] = useState<any[]>([]);
  const [showAddEvent, setShowAddEvent] = useState(false);
  const [channelId, setChannelId] = useState("");
  // v1
  const current = new Date();
  const [description, setDescription] = useState("");
  const [recurring, setRecurring] = useState(false);
  const [frequency, setFrequency] = useState("1-W");
  const [repeatTill, setRepeatTill] = useState(new Date());
  const [isMeeting, setIsMeeting] = useState(false);
  const [recordMeeting, setRecordMeeting] = useState(false);
  const [isCreatingEvents, setIsCreatingEvents] = useState(false);
  const [editEvent, setEditEvent] = useState<any>(null);
  // Stores channel name of event being modified
  const [editChannelName, setEditChannelName] = useState("");
  const [isEditingEvents, setIsEditingEvents] = useState(false);
  const [isDeletingEvents, setIsDeletingEvents] = useState(false);

  // const [viewModel, setViewModel] = useState<any>(new SchedulerData(new moment().format(DATE_FORMAT), ViewTypes.Week))

  const localizer = momentLocalizer(moment);

  const loadChannels = useCallback(async () => {
    const uString: any = await AsyncStorage.getItem("user");
    if (uString) {
      const user = JSON.parse(uString);
      const server = fetchAPI("");
      server
        .query({
          query: getChannels,
          variables: {
            userId: user._id,
          },
        })
        .then((res) => {
          if (res.data.channel.findByUserId) {
            setChannels(res.data.channel.findByUserId);
          }
        })
        .catch((err) => {});
    }
  }, []);

  useEffect(() => {
    if (title !== "" && end > start) {
        setIsSubmitDisabled(false);
        return;
    }
    setIsSubmitDisabled(true);
  }, [title, start, end]);

  let resources = [
    {
      id: "lectures",
      name: "Lectures",
    },
    {
      id: "submissions",
      name: "Submissions",
    },
    {
      id: "others",
      name: "Others",
    },
  ];

  // useEffect(() => {
  //     if (!events || events.length === 0) return;
  //     console.log(events);

  //     const schedulerData = new SchedulerData(new moment().format(DATE_FORMAT), ViewTypes.Week);

  //     schedulerData.setResources(resources)

  //     schedulerData.setEvents(events)

  //     console.log(schedulerData)

  //     setViewModel(schedulerData)

  // }, [events])

  // use effect for edit events
  useEffect(() => {
    if (editEvent) {
      setTitle(editEvent.originalTitle);
      setDescription(editEvent.description);
