      return;
    }
    setIsCreatingEvents(true);

    const meeting = channelId && channelId !== "" ? isMeeting : false;

    const freq = recurring ? frequency : "";

    const repeat = recurring ? repeatTill.toUTCString() : "";

    const u = await AsyncStorage.getItem("user");
    if (u) {
      const user = JSON.parse(u);
      const server = fetchAPI("");
      server
        .mutate({
          mutation: createDateV1,
          variables: {
            title,
            userId: user._id,
            start: start.toUTCString(),
            end: end.toUTCString(),
            channelId,
            meeting,
            description,
            recordMeeting,
            frequency: freq,
            repeatTill: repeat,
          },
        })
        .then((res) => {
          const updated = new Date();

          loadEvents();
          setTitle("");
          setRepeatTill(new Date());
          setIsMeeting(false);
          setDescription("");
          setFrequency("1-W");
          setRecurring(false);
          setRecordMeeting(false);
          setIsCreatingEvents(false);
          setShowAddEvent(false);
        })
        .catch((err) => {
          setIsCreatingEvents(false);
          console.log(err);
        });
    }
