    input: {
        width: "100%",
        borderBottomColor: "#f4f4f6",
        borderBottomWidth: 1,
        fontSize: 15,
        padding: 15,
        paddingTop: 12,
        paddingBottom: 12,
        marginBottom: 20
    },
    text: {
        fontSize: 12,
        color: "#a2a2aa",
        textAlign: "left",
        paddingHorizontal: 10
    },
    allBlack: {
        fontSize: 12,
        color: "#202025",
        height: 22,
        paddingHorizontal: 10,
        backgroundColor: "white"
    },
    allOutline: {
        fontSize: 12,
        color: "#FFF",
        height: 22,
        paddingHorizontal: 10,
        borderRadius: 10,
        backgroundColor: "#202025"
    },
    picker: {
        display: "flex",
        justifyContent: "flex-start",
        backgroundColor: "white",
        overflow: "hidden",
        fontSize: 12,
        textAlign: "center",
        borderWidth: 1,
        width: 150,
        height: 20,
        alignSelf: "center",
        marginTop: 0,
        borderRadius: 3
    }
});
